import React from 'react';

const MovieList = ({movies}) => {
    let {id, title, poster_path, release_date} = movies;

    const image = movie => movie.poster_path ? 
        `https://image.tmdb.org/t/p/w200${movie.poster_path}` : 
        'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQV6FXfa9Qtj8Ns-ZtGZvV7yQWnlmvmzysk0GSWPhbrq332oAE-&usqp=CAU';
     
    return (
        movies ? movies.map(movie => 
            <div key={id} className="card col-md-2 m-3">
                <img 
                    style={{ height: 230}}
                    src={image(movie)}
                    className="card-img-top card-img" 
                    alt={title} 
                />
                <div className="card-body">
                    <h6 className="text-primary">{title} ({release_date.substring(0, 4)})</h6>
                </div>
            </div>
        ) : null
    )
}

export default MovieList;
