import React, { Component } from 'react';
import Header from './components/Header';
import MovieSearch from './components/MovieSearch';

import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends Component {
  apiKey = 'ef2bb6c94175465f3015e8e71a9b457c';

  state = {
    movies: [],
    inputValue: '' || 'terminator'
  }

  getMovieData = async e => {
    e.preventDefault();

    let res = await fetch(`https://api.themoviedb.org/3/search/movie?api_key=${this.apiKey}&language=en-US&query=${this.state.inputValue}&page=1&include_adult=false`);
    let data = await res.json();
    this.setState({ movies: data.results });
    console.log(this.state.movies);

  }

  getInputValue = e => {
    this.setState({ inputValue: e.target.value });
  }


  render() {
    return (
      <div>
        <Header />
        <MovieSearch 
          getInputValue={this.getInputValue} 
          getMovieData={this.getMovieData} 
        />
      </div>
    )
  }
}

export default App;
