import React from 'react';

const MovieList = ({movies}) => {
    
    const name = movie => {
        if (movie.poster_path) {
            return `https://image.tmdb.org/t/p/w500${movie.poster_path}`;
        } else {
            return 'https://thumbs.dreamstime.com/b/no-image-available-icon-flat-vector-no-image-available-icon-flat-vector-illustration-132482930.jpg'
        }
    }
     
    return (
        movies.map(movie => 
            <div key={movie.id} className="card col-md-2 m-3">
                <img 
                    style={{ height: 230}}
                    // src={`https://image.tmdb.org/t/p/w500${movie.poster_path ? 
                    //     movie.poster_path : 'https://thumbs.dreamstime.com/b/no-image-available-icon-flat-vector-no-image-available-icon-flat-vector-illustration-132482930.jpg'}`
                    // } 
                    src={name(movie)}
                    className="card-img-top card-img" 
                    alt={movie.title} 
                />
                <div className="card-body">
                    {/* <p class="card-text text-dark "></p> */}
                    <p className="text-primary">{movie.title}</p>
                </div>
            </div>
        )
    )
}

export default MovieList;
